mapper.update:
update tbplay
set streamaddress = #{streamaddress,jdbcType=VARCHAR}
where id =
#{id,jdbcType=INTEGER}

mapper.select:
select

from tbplay

	del_flag='0'

		and playerid = #{playerId}


		and status = #{status}


		and title like #{playTitle}


		and DATE_FORMAT(begintime,'%Y-%m-%d %H:%i:%S') >=
		#{beginTime}


		and #{endTime}>=DATE_FORMAT(endTime,'%Y-%m-%d %H:%i:%S')


order by id desc
